import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Registration = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        phone: '',
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async () => {
        try {
            setLoading(true);

            // Simple form validation
            if (!formData.username || !formData.email || !formData.password) {
                setError('Please fill in all required fields.');
                return;
            }

            const response = await axios.post("http://localhost:3001/registration", formData);

            if (response.data.message === "User registered successfully") {
                navigate('/login');
            } else {
                setError('Registration failed. Please check your details and try again.');
            }
        } catch (error) {
            console.error(error);
            setError('Error occurred during registration. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            {/* Your JSX code for the registration form goes here */}
            <label>Username:</label>
            <input type="text" name="username" value={formData.username} onChange={handleChange} />

            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} />

            <label>Password:</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} />

            <label>Phone:</label>
            <input type="tel" name="phone" value={formData.phone} onChange={handleChange} />

            {error && <div className="text-danger">{error}</div>}

            <button disabled={loading} onClick={handleSubmit}>
                {loading ? 'Signing up...' : 'Sign up'}
            </button>

            <p>or sign up with: <Link to="/login">Sign in</Link></p>
        </div>
    );
};

export default Registration;
