import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCol,
  MDBRow,
  MDBInput,
  MDBCheckbox
} from 'mdb-react-ui-kit';

function Registration() {
  const [formData, setFormData] = useState({
    username: '',
    phone: '',
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleSignUp = async () => {
    try {
      const response = await axios.post("http://localhost:3001/Registration", formData);
  
      if (response.data.message === "User registered successfully") {
        // Navigate to the login page
        navigate("/login");
      } else if (response.data.message === "exist") {
        alert("User already exists");
      } else if (response.data.message === "notexist") {
        alert("User does not exist");
      }
    } catch (error) {
      alert("Wrong details");
      console.error(error);
    }
  };
  
  return (
    <MDBContainer fluid>
      <div className="p-5 bg-image" style={{backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px'}}></div>

      <MDBCard className='mx-5 mb-5 p-5 shadow-5' style={{marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)'}}>
        <MDBCardBody className='p-5 text-center'>

          <h2 className="fw-bold mb-5">Sign up now</h2>

          <MDBRow>
            <MDBCol col='6'>
              <MDBInput
                wrapperClass='mb-4'
                label='First name'
                id='form1'
                type='text'
                value={formData.username}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
              />
            </MDBCol>

            <MDBCol col='6'>
              <MDBInput
                wrapperClass='mb-4'
                label='phone'
                id='form2'
                type='text'
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
              />
            </MDBCol>
          </MDBRow>

          <MDBInput
            wrapperClass='mb-4'
            label='Email'
            id='form3'
            type='email'
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
          <MDBInput
            wrapperClass='mb-4'
            label='Password'
            id='form4'
            type='password'
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />

          <div className='d-flex justify-content-center mb-4'>
            <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Subscribe to our newsletter' />
          </div>

          <MDBBtn className='w-100 mb-4' size='md' onClick={handleSignUp}>
            Sign up
          </MDBBtn>

          <div className="text-center">
            <p>or sign up with:</p>
            {/* ... Social media sign up buttons ... */}
          </div>

        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
}

export default Registration;
