import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCol,
  MDBRow,
  MDBInput,
} from 'mdb-react-ui-kit';

const Registration = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    phone: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);

      const response = await axios.post("http://localhost:3001/Registration", formData);

      if (response.data === "success") {
        navigate('/login');
      } else {
        setError('Registration failed. Please check your details and try again.');
      }
    } catch (error) {
      console.error(error);
      setError('Error occurred during registration. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <MDBContainer fluid>
      <div className="p-5 bg-image" style={{ backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px' }}></div>
      <MDBCard className='mx-5 mb-5 p-5 shadow-5' style={{ marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)' }}>
        <MDBCardBody className='p-5 text-center'>
          <h2 className="fw-bold mb-5">Sign up now</h2>
          <MDBRow>
            <MDBCol col='6'>
              <MDBInput wrapperClass='mb-4' label='Username' id='form2' type='text' name="username" onChange={handleChange} />
            </MDBCol>
          </MDBRow>
          <MDBInput wrapperClass='mb-4' label='Email' id='form3' type='email' name="email" onChange={handleChange} />
          <MDBInput wrapperClass='mb-4' label='Password' id='form4' type='password' name="password" onChange={handleChange} />
         

          {error && <div className="text-danger mb-3">{error}</div>}

          <MDBBtn disabled={loading} className='w-100 mb-4' size='md' onClick={handleSubmit}>
            {loading ? 'Signing up...' : 'Sign up'}
          </MDBBtn>

          <div className="text-center">
            <p>or sign up with:</p>
            <Link to="/login" className="mx-2">Sign in</Link>
          </div>
        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
};

export default Registration;
