// ForgotPassword.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleResetPassword = async (e) => {
    e.preventDefault();

    try {
      // Check if the email exists
      const emailCheckResponse = await axios.post('http://localhost:3001/api/check-email', { email });

      if (emailCheckResponse.data.exists) {
        // Email exists, validate and update the new password
        if (newPassword === confirmPassword) {
          // Make an API request to update the new password in the database
          const updateResponse = await axios.post('http://localhost:3001/api/update-password', {
            email,
            newPassword,
          });

          setMessage(updateResponse.data.message);
        } else {
          setMessage('Passwords do not match.');
        }
      } else {
        setMessage('Email not found.');
      }
    } catch (error) {
      console.error('Error resetting password:', error.message);
      setMessage('An error occurred while resetting the password.');
    }
  };

  return (
    <div>
      <h2>Forgot Password</h2>
      <form onSubmit={handleResetPassword}>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        {/* New Password */}
        <label htmlFor="newPassword">New Password:</label>
        <input
          type="password"
          id="newPassword"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          required
        />
        {/* Confirm Password */}
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input
          type="password"
          id="confirmPassword"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
      <p>{message}</p>
      <Link to="/login">Back to Login</Link>
    </div>
  );
}

export default ForgotPassword;
