import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { MDBBtn, MDBContainer, MDBCard, MDBCardBody, MDBCardImage, MDBRow, MDBCol, MDBIcon, MDBInput } from 'mdb-react-ui-kit';
import axios from 'axios';
import Cookies from 'universal-cookie';

function Login() {
  const history = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const cookies = new Cookies();

  const submit = async (e) => {
    e.preventDefault();

    // Add a validation check for empty email or password
    if (!email || !password) {
      alert("Please enter both email and password");
      return;
    }

    try {
      const response = await axios.post("http://localhost:3001/login", {
        email,
        password,
      });

      if (response.data.message === "Login successful") {
        cookies.set("user", email, { path: "/" });
        sessionStorage.setItem("email", email);
        history("/Dashboard", { state: { id: email } });
      } else if (response.data.message === "User not found") {
        alert("User not found");
      } else if (response.data.message === "Invalid password") {
        alert("Invalid password");
      }
    } catch (error) {
      alert("Error occurred. Please check your details and try again.");
      console.error(error);
    }
  };

  return (
    <MDBContainer className="my-5">
      <MDBRow className="justify-content-center">
        <MDBCol md="6">
          <MDBCard>
            <MDBCardImage
              src="https://cdn.pixabay.com/photo/2018/09/04/10/27/never-stop-learning-3653430_640.jpg"
              alt="MDB Card image cap"
              top
              hover
              overlay="white-slight"
            />
            <MDBCardBody>
              <div className="text-center">
                <MDBIcon fas icon="cubes fa-3x" style={{ color: '#ff6219' }} />
                <h2 className="h1-responsive fw-bold mb-4">Logo</h2>
              </div>
              <form>
                <div className="mb-4">
                  <MDBInput
                    label="Email"
                    id="formControlLg"
                    type="email"
                    size="lg"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
                <div className="mb-4">
                  <MDBInput
                    label="Password"
                    id="formControlLg"
                    type="password"
                    size="lg"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>
                <div className="text-center">
                  <MDBBtn color="dark" size="lg" onClick={submit} disabled={!email || !password}>
                    Login
                  </MDBBtn>
                </div>
              </form>
              <div className="mt-3 text-center">
                <p className="small text-muted">
                  Don't have an account? <Link to="/registration">Register here</Link>
                </p>
                <p className="small text-muted">
                  Forgot your password? <Link to="/forgot-password">Reset it here</Link>
                </p>
              </div>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default Login;
