import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { MDBBtn, MDBContainer, MDBCard, MDBCardBody, MDBCardImage, MDBRow, MDBCol, MDBIcon, MDBInput } from 'mdb-react-ui-kit';
import axios from 'axios';
import Cookies from 'universal-cookie';

function Login() {
  const history = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const cookies = new Cookies();

  const submit = async (e) => {
    e.preventDefault();

    // Add a validation check for empty email or password
    if (!email || !password) {
      alert("Please enter both email and password");
      return;
    }

    try {
      const response = await axios.post("http://localhost:3001/login", {
        email,
        password,
      });

      if (response.data.message === "Login successful") {
        cookies.set("user", email, { path: "/" });
        sessionStorage.setItem("email", email);
        history("/Dashboard", { state: { id: email } });
      } else if (response.data.message === "User not found") {
        alert("User not found");
      } else if (response.data.message === "Invalid password") {
        alert("Invalid password");
      }
    } catch (error) {
      alert("Error occurred. Please check your details and try again.");
      console.error(error);
    }
  };

  return (
    <MDBContainer fluid>
      {/* Use your education-related background image */}
      <div className="p-5 bg-image" style={{backgroundImage: 'url(path-to-your-education-image.jpg)', height: '300px'}}></div>

      <MDBCard className='mx-5 mb-5 p-5 shadow-5' style={{marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)'}}>
        <MDBCardBody className='p-5 text-center'>

          <div className='d-flex flex-row mt-2'>
            <MDBIcon fas icon="cubes fa-3x me-3" style={{ color: '#ff6219' }}/>
            <span className="h1 fw-bold mb-0">Logo</span>
          </div>

          <h5 className="fw-normal my-4 pb-3" style={{letterSpacing: '1px'}}>Sign into your account</h5>

          <MDBInput wrapperClass='mb-4' label='Email address' id='formControlLg' type='email' size="lg" value={email} onChange={(e) => setEmail(e.target.value)} />
          <MDBInput wrapperClass='mb-4' label='Password' id='formControlLg' type='password' size="lg" value={password} onChange={(e) => setPassword(e.target.value)} />

          <MDBBtn className='w-100 mb-4' size='md' onClick={submit}>
            Login
          </MDBBtn>

          <div className="text-center">
            <p>Don't have an account? <Link to="/registration">Sign up</Link></p>
          </div>

        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
}

export default Login;
