import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Registration.css';

function Registration() {
  const [formData, setFormData] = useState({
    username: '',
    phone: '',
    email: '',
    password: '',
    Catgeroy: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (fieldName, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [fieldName]: value,
    }));
  };

  const handleRegistration = async () => {
    try {
      const response = await axios.post("http://localhost:3001/Registration", formData);

      if (response.data.message === "User registered successfully") {
        navigate("/");
      } else if (response.data.message === "exist") {
        alert("User already exists");
      } else if (response.data.message === "notexist") {
        alert("User does not exist");
      }
    } catch (error) {
      alert("Wrong details");
      console.error(error);
    }
  };

  return (
    <div>
      <header className="header">
        <div className="logo-container">
           <img src="data:image/png;base64," alt="Logo" className="logo" />
          <h1>Library Management System</h1>
        </div>
      </header>
      <nav className="navbar">
        <a href="#home">Home</a>
        <a href="#gallery">Gallery</a>
        <a href="#how-it-works">How It Works</a>
        <a href="#plans">Plans</a>
        <a href="#about">About</a>
        <a href="#contact-us">Contact Us</a>
      </nav>
      <div className="content-container">
        <div className="book-image">
          <img src="https://i0.wp.com/stanzaliving.wpcomstaging.com/wp-content/uploads/2022/04/fc23e-library-in-jaipur.jpg?fit=1000%2C667&ssl=1" alt="Person touching a virtual screen" height="600" width="900" />
        </div>
        <div className="login-container">
          <div className="login-form">
              <img  src="data:image/jpeg;base64," alt="Login Logo" className="login-logo" height={60}/>
            <form action="Registration" method="post">
              <label>Username</label>
              <input type="text" name="Username" value={formData.username} onChange={(e) => handleInputChange('username', e.target.value)} />
              <label>Phone</label>
              <input type="text" name="Phone" value={formData.phone} onChange={(e) => handleInputChange('phone', e.target.value)} />
              <label>Email</label>
              <input type="email" name="email" value={formData.email} onChange={(e) => handleInputChange('email', e.target.value)} />
              <label>Password</label>
              <input type="password" name="password" value={formData.password} onChange={(e) => handleInputChange('password', e.target.value)} />
              <label>Category</label>
              <select className='dik' value={formData.Catgeroy} onChange={(e) => handleInputChange('Catgeroy', e.target.value)}>
                <option>Teacher</option>
                <option>Student</option>
                <option>Faculty</option>
              </select>
              <br></br>
              <br></br>
              <button type="button" onClick={handleRegistration}>Register</button>
              <div className="mt-3 text-center">
                <p className="small text-muted">
                  Already have an account? <Link to="/">Login here</Link>
                </p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Registration;
