import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'universal-cookie'; // Import Cookies

import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBInput
} from 'mdb-react-ui-kit';

function Login() {
  const history = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const cookies = new Cookies(); // Initialize cookies

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:3001/login", {
        email,
        password,
      });

      if (response.data.message === "User registered successfully") {
        cookies.set("user", email, { path: "/" }); // Set a cookie
        sessionStorage.setItem("email", email); // Save the user ID in session storage
        history("/Dashboard", { state: { id: email } });
      } else if (response.data.message === "notexist") {
        alert("User has not signed up");
      }
    } catch (error) {
      alert("Error occurred. Please check your details and try again.");
      console.error(error);
    }
  }

  return (
    <MDBContainer fluid>
      <div className="p-5 bg-image" style={{backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px'}}></div>

      <MDBCard className='mx-5 mb-5 p-5 shadow-5' style={{marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)'}}>
        <MDBCardBody className='p-5 text-center'>

          <h2 className="fw-bold mb-5">Sign IN</h2>

          <MDBInput wrapperClass='mb-4' label='Email' id='form3' type='email' value={email} onChange={(e) => setEmail(e.target.value)} />
          <MDBInput wrapperClass='mb-4' label='Password' id='form4' type='password' value={password} onChange={(e) => setPassword(e.target.value)} />

          <MDBBtn className='w-100 mb-4' size='md' onClick={submit}>
            sign In
          </MDBBtn>

          <div className="text-center">
            <p>Don't have an account? <Link to="/registration">Sign up</Link></p>
            {/* ... Social media sign-in buttons ... */}
          </div>

        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
}

export default Login;
