import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBInput
} from 'mdb-react-ui-kit';

function Registration() {
  const [formData, setFormData] = useState({
    username: '',
    phone: '',
    email: '',
    password: '',
  });

  
  const navigate = useNavigate();

  const handleSignUp = async () => {
    try {
      const response = await axios.post("http://localhost:3001/Registration", formData);
  
      if (response.data.message === "User registered successfully") {
        // Navigate to the login page
        navigate("/");
      } else if (response.data.message === "exist") {
        alert("User already exists");
      } else if (response.data.message === "notexist") {
        alert("User does not exist");
      }
    } catch (error) {
      alert("Wrong details");
      console.error(error);
    }
  };

 

  return (
    <MDBContainer className="my-5">
      <MDBCard>
        <MDBRow className='g-0'>
          <MDBCol md='6'>
            <MDBCardImage src='https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/img1.webp' alt="registration form" className='rounded-start w-100' />
          </MDBCol>

          <MDBCol md='6'>
            <MDBCardBody className='d-flex flex-column'>
              <div className='d-flex flex-row mt-2'>
                <MDBIcon fas icon="cubes fa-3x me-3" style={{ color: '#ff6219' }} />
                <span className="h1 fw-bold mb-0">Logo</span>
              </div>

              <h5 className="fw-normal my-4 pb-3" style={{ letterSpacing: '1px' }}>Create your account</h5>

              <MDBInput
                wrapperClass='mb-4'
                label='Username'
                id='username'
                type='text'
                size="lg"
                value={formData.username}
                onChange={(e) => handleInputChange('username', e.target.value)}
              />
              <MDBInput
                wrapperClass='mb-4'
                label='Phone'
                id='phone'
                type='text'
                size="lg"
                value={formData.phone}
                onChange={(e) => handleInputChange('phone', e.target.value)}
              />
              <MDBInput
                wrapperClass='mb-4'
                label='Email address'
                id='email'
                type='email'
                size="lg"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
              />
              <MDBInput
                wrapperClass='mb-4'
                label='Password'
                id='password'
                type='password'
                size="lg"
                value={formData.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
              />

              <MDBBtn className="mb-4 px-5" color='dark' size='lg' onClick={handleSignUp}>
                Sign up
              </MDBBtn>
              <p className="mb-5 pb-lg-2" style={{ color: '#393f81' }}>Already have an account? <a href="#!" style={{ color: '#393f81' }}>Login here</a></p>

              <div className='d-flex flex-row justify-content-start'>
                <a href="#!" className="small text-muted me-1">Terms of use.</a>
                <a href="#!" className="small text-muted">Privacy policy</a>
              </div>
            </MDBCardBody>
          </MDBCol>
        </MDBRow>
      </MDBCard>
    </MDBContainer>
  );
}

export default Registration;
