import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { MDBBtn, MDBContainer, MDBCard, MDBCardBody, MDBCardImage, MDBRow, MDBCol, MDBIcon, MDBInput } from 'mdb-react-ui-kit';
import axios from 'axios';
import Cookies from 'universal-cookie';
import './login.css';

function Login() {
  const history = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const cookies = new Cookies();

  const submit = async (e) => {
    e.preventDefault();

    // Add a validation check for empty email or password
    if (!email || !password) {
      alert("Please enter both email and password");
      return;
    }

    try {
      const response = await axios.post("http://localhost:3001/login", {
        email,
        password,
      });

      if (response.data.message === "Login successful") {
        cookies.set("user", email, { path: "/" });
        sessionStorage.setItem("email", email);
        history("/Dashboard", { state: { id: email } });
      } else if (response.data.message === "User not found") {
        alert("User not found");
      } else if (response.data.message === "Invalid password") {
        alert("Invalid password");
      }
    } catch (error) {
      alert("Error occurred. Please check your details and try again.");
      console.error(error);
    }
  };

  return (
    <MDBContainer className="my-5" style={{ maxWidth: '600px', backgroundColor: '#9A616D' }}>
      <MDBCard>
        <MDBRow className='g-0'>
          <MDBCol md='6'>
            <MDBCardImage src='https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/img1.webp' alt="login form" className='rounded-start w-100' />
          </MDBCol>
          <MDBCol md='6'>
            <MDBCardBody className='d-flex flex-column'>
              <div className='d-flex flex-row mt-2'>
                <img src="https://st2.depositphotos.com/1378583/5228/v/450/depositphotos_52283153-stock-illustration-hand-book-logo.jpg" alt="Logo" className="me-3" style={{ height: '3rem', width: 'auto' }} />
              </div>

              <h5 className="fw-normal my-4 pb-3 text-center" style={{ letterSpacing: '1px' }}>
                Sign into your account
              </h5>

              <MDBInput wrapperClass='mb-4' label='Email address' id='formControlLg' type='email' size="lg" value={email} onChange={(e) => setEmail(e.target.value)} />
              <MDBInput wrapperClass='mb-4' label='Password' id='formControlLg' type='password' size="lg" value={password} onChange={(e) => setPassword(e.target.value)} />
              {/* Disable the button if email or password is empty */}
              <MDBBtn className="mb-4 px-5" color='dark' size='lg' onClick={submit} disabled={!email || !password}>
                Login
              </MDBBtn>
              <div className="mt-3 text-center">
                <p className="small text-muted">
                  Don't have an account? <Link to="/registration">Register here</Link>
                </p>
                <p className="small text-muted">
                  Forgot your password? <Link to="/forgot-password">Reset it here</Link>
                </p>
              </div>
            </MDBCardBody>
          </MDBCol>
        </MDBRow>
      </MDBCard>
    </MDBContainer>
  );
}

export default Login;
