// Login.js

import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Cookies from 'universal-cookie';
import { Link } from 'react-router-dom';
import { MDBBtn, MDBContainer, MDBCard, MDBCardBody, MDBInput } from 'mdb-react-ui-kit';

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const cookies = new Cookies();

  async function submit(e) {
    e.preventDefault();

    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }

    try {
      setLoading(true);

      const response = await axios.post("http://localhost:3001/login", {
        email,
        password,
      });

      if (response.data === "exist") {
        cookies.set("user", email, { path: "/" });
        sessionStorage.setItem("email", email);
        navigate("/dashboard", { state: { id: email } });
      } else if (response.data === "notexist") {
        setError('User does not exist. Please sign up.');
      }
    } catch (error) {
      setError('Error occurred. Please check your details and try again.');
      console.error(error);
    } finally {
      setLoading(false);
    }
  }

  return (
    <MDBContainer fluid>
      <div className="p-5 bg-image" style={{ backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px' }}></div>
      <MDBCard className='mx-5 mb-5 p-5 shadow-5' style={{ marginTop: '-100px', background: 'hsla(0, 0%, 100%, 0.8)', backdropFilter: 'blur(30px)' }}>
        <MDBCardBody className='p-5 text-center'>
          <h2 className="fw-bold mb-5">Sign IN</h2>
          <MDBInput
            wrapperClass='mb-4'
            label='Email'
            id='emailInput'
            type='email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <MDBInput
            wrapperClass='mb-4'
            label='Password'
            id='passwordInput'
            type='password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          
          {error && <div className="text-danger mb-3">{error}</div>}

          <MDBBtn disabled={loading} onClick={submit} className='w-100 mb-4' size='md'>
            {loading ? 'Signing in...' : 'Sign in'}
          </MDBBtn>

          <div className="text-center">
            <p>or sign up with:</p>
            <Link to="/registration" className="mx-2">Sign Up</Link>
          </div>
        </MDBCardBody>
      </MDBCard>
    </MDBContainer>
  );
}

export default Login;
